    constructor(props) {
        super(props);
        this.state = { data: {}};
    }

    componentDidMount() {
        Promise.all([d3.csv(Data)])
            .then(resolved_data => {
                var data = resolved_data[0]
                this.setState({ data: data });
            })
            .catch(error => console.error("Error loading data: ", error));
    }
