                    <option value="Apple">Apple</option>
                    <option value="Amazon">Amazon</option>
                    <option value="Google">Google</option>
                    <option value="Meta">Meta</option>
                    <option value="Microsoft">Microsoft</option>
                </select>
                {parsedData && all_data[selected_company] && <Child1 data={all_data} selectedCompany={selected_company} parsedData={parsedData} />}
                {parsedData && all_data[selected_company] && <Child2 data={all_data} selectedCompany={selected_company} parsedData={parsedData} />}

            </div>
        );
    }
}

export default App;
