// App component
import React, { Component } from 'react';
import Child1 from './Child1';
import Child2 from './Child2';
import * as d3 from 'd3';
import Data from './data/SampleDataset.csv';


class App extends Component {
    constructor(props) {
        super(props);
        this.state = { data: {}, category: 'A'};
    }

    componentDidMount() {
        Promise.all([d3.csv(Data)])
            .then(resolved_data => {
                var data = resolved_data[0]
                this.setState({ data: data });
            })
            .catch(error => console.error("Error loading data: ", error));
    }
    conponentDidUpdate(){
        var data = this.state.data;
        var category = this.state.category;
        var parsedData = data.filter((item) => item.category == category);
          console.log(parsedData);
          var x_data = parsedData.map(item => item.X);
          var y_data = parsedData.map(item => item.Y);
          var xAxis = d3.axisBottom(d3.scaleLinear().domain(d3.extent(x_data)).range([0,300]));
          var yAxis = d3.axisLeft(d3.scaleLinear().domain(d3.extent(y_data)).range([0,300]));
          d3.select("#scatter").selectAll("circle").data(parsedData)
          .attr("x", d => d.x)
          .attr("y", d => d.y)
          .attr("fill", "green")
    }
    render() {
        const {data, category} = this.state;
        return (
            <div>
                <select onChange={(event) => this.setState({ category: event.target.value })}>
                    <option value="A">A</option>
                    <option value="B">B</option>
                    <option value="C">C</option>
                </select>
                <svg className="scatter" width="760" height="600"></svg>
                {category && <Child2 data={data} category={category}/>}
            </div>
        );
    }
}
//

//{data && <Child1 data={data} category={category}/>}
export default App;
