import React, { Component } from 'react';
import * as d3 from 'd3';

class Child1 extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {
        this.componentDidUpdate();
    }

    componentDidUpdate() {
        const { data, selectedCompany, parsedData } = this.props;
        d3.select('#scatter').selectAll('*').remove();

        if (!parsedData[selectedCompany]) {
            parsedData[selectedCompany] = data[selectedCompany].map(item => {
                var parseDate = d3.timeParse("%Y-%m-%d");
                item.Date = parseDate(item.Date);
                item.Open = parseFloat(item.Open);
                item.Close = parseFloat(item.Close);
                return item;
            });
        }
        const x_axis_data = parsedData[selectedCompany].map(item => item.Date);
        const y_axis_data = parsedData[selectedCompany].map(item => item.Open);

        const x_scale = d3.scaleTime().domain(d3.extent(x_axis_data)).range([0, 400]);
        const y_scale = d3.scaleLinear().domain([d3.min(y_axis_data), d3.max(y_axis_data)]).range([300, 0]);

        const svg = d3.select("#scatter");

        svg.selectAll(".open")
            .data(parsedData[selectedCompany])
            .enter()
            .append('circle')
            .attr('class', 'open')
            .attr('cx', d => x_scale(d.Date))
            .attr('cy', d => y_scale(d.Open))
            .attr('r', 5)
            .attr('fill', 'green');

        svg.selectAll(".close")
            .data(parsedData[selectedCompany])
            .enter()
            .append('circle')
            .attr('class', 'close')
            .attr('cx', d => x_scale(d.Date))
            .attr('cy', d => y_scale(d.Close))
            .attr('r', 5)
            .attr('fill', 'red');
    }

    render() {
        return <svg id='scatter' width="500" height="300"></svg>;
    }
}

export default Child1;
