import React, { Component } from 'react';
import * as d3 from 'd3';

class Child2 extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {
      //this.componentDidUpdate()
    }

    componentDidUpdate() {
          var category = this.props.category
          var data = this.props.data
          d3.select('#scatter').selectAll('*').remove();
          var parsedData = data.filter((item) => item.category == category);
          console.log(parsedData);
          var x_data = parsedData.map(item => item.X);
          var y_data = parsedData.map(item => item.Y);
          var xAxis = d3.axisBottom(d3.scaleLinear().domain(d3.extent(x_data)).range([0,300]));
          var yAxis = d3.axisLeft(d3.scaleLinear().domain(d3.extent(y_data)).range([0,300]));
          d3.select("#scatter").join('g').join(xAxis).join(yAxis).data()

    }

    render() {
        return <svg id='demo2' width="500" height="500"></svg>;
    }
}

export default Child2;
