import React, { Component } from 'react';
import * as d3 from 'd3';

class Child2 extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {

    }

    componentDidUpdate() {
          const { data, selectedCompany } = this.props;
          d3.select('#demo2').selectAll('*').remove();

          console.log(selectedCompany, data)
          const parsedData = data[selectedCompany].map(item => {
              var parseDate = d3.timeParse("%Y-%m-%d");
              item.Date = parseDate(item.Date);
              item.Open = parseFloat(item.Open);
              item.Close = parseFloat(item.Close);
              return item;
          });
          //console.log(parsedData)
          const x_axis_data = parsedData.map(item => item.Date);
          console.log(x_axis_data)
          const open_data = parsedData.map(item => item.Open);
          const close_data = parsedData.map(item => item.Close);
          const open_close_combined = [...open_data, ...close_data];
          //console.log(open_close_combined)
          const x_scale = d3.scaleTime().domain().range([0, 400]);
          const y_scale = d3.scaleLinear().domain(d3.extent(open_close_combined)).range([300, 0]);
          const colormap = { "open": "green", "close": "red" };

          /*d3.select('#demo2').selectAll('.lines_g').data([parsedData, parsedData]).join('g')
              .attr('class', 'lines_g')
              .each(function(area_chart_data, i) {
                  const line = d3.line()
                      .x(d => x_scale(d.Date))
                      .y(d => i === 0 ? y_scale(d.Open) : y_scale(d.Close));
                  d3.select(this).selectAll('.line')
                      .data([area_chart_data])
                      .join('path')
                      .attr("class", "line")
                      .attr("d", line)
                      .attr("fill", 'none')
                      .attr('stroke', i === 0 ? 'green' : 'red')
                      .attr('stroke-width', 1);
              });*/
              d3.select("#demo2").selectAll('.lines_g').data([parsedData,parsedData]).join('g').attr('class', 'lines_g').attr("add_line", function(area_chart_data,i){
                if (i == 0){
                  const line = d3.line().x(d=>x_scale(d.Date)).y(d=>y_scale(d.Open))
                  d3.select(this).selectAll('.line').data([area_chart_data]).join('path').attr("class","line").attr("d",line).attr("fill", 'none').attr('stroke', 'green').attr('stroke-width',1)
                }
                 else{
                  const line = d3.line().x(d=>x_scale(d.Date)).y(d=>y_scale(d.Close))
                  d3.select(this).selectAll('.line').data([area_chart_data]).join('path').attr("class","line").attr("d",line).attr("fill", 'none').attr('stroke', 'red').attr('stroke-width',1)
                 } 
                })
                d3.select(".demo2").selectAll(".legend_g").data([0]).join('g').attr('class', 'legend_g').attr("transform", "translate(400,20)")
                .selectAll('.item').data(["open","close"]).join("g").attr('class', 'item').attr('transform', (dd,i)=>`translate(0,${i*25})`)
                .attr('add_items', function(dd){
                  d3.select(this).selectAll("rect").data([0]).join('rect').attr('x',0).attr('width',10).attr('height',10).attr('fill',d=>colormap[dd])
                  d3.select(this).selectAll("text").data([0]).join('text').attr('x',20).attr('y',10).text(d=>dd)
                })
    }

    render() {
        return <svg id='demo2' width="500" height="300"></svg>;
    }
}

export default Child2;
