    return (
      <div>
      <select> 
        <option> Apple </option>
        <option> Amazon </option>
        <option> Google </option>
        <option> Meta </option>
        <option> Microsoft </option>
      </select>
      <svg id="demo2" width="300" height="300"></svg>
      </div>
    );
  }
}

export default App;
