// App component
import React, { Component } from 'react';
import Child2 from './Child2';
import AAPL from './data/AAPL.csv';
import AMZN from './data/AMZN.csv';
import GOOGL from './data/GOOGL.csv';
import META from './data/META.csv';
import MSFT from './data/MSFT.csv';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = { all_data: {}, selected_company: "Google" };
    }

    componentDidMount() {
        Promise.all([d3.csv(AAPL), d3.csv(AMZN), d3.csv(GOOGL), d3.csv(META), d3.csv(MSFT)])
            .then(resolved_data => {
                var temp_data = {
                    Apple: resolved_data[0],
                    Amazon: resolved_data[1],
                    Google: resolved_data[2],
                    Meta: resolved_data[3],
                    Microsoft: resolved_data[4]
                };
                this.setState({ all_data: temp_data });
            })
            .catch(error => console.error("Error loading data: ", error));
    }

    render() {
        const { all_data, selected_company } = this.state;
        return (
            <div>
                <select onChange={(event) => this.setState({ selected_company: event.target.value })}>
                    <option value="Apple">Apple</option>
                    <option value="Amazon">Amazon</option>
                    <option value="Google">Google</option>
                    <option value="Meta">Meta</option>
                    <option value="Microsoft">Microsoft</option>
                </select>
                {all_data[selected_company] && <Child2 data={all_data} selectedCompany={selected_company} />}
            </div>
        );
    }
}

export default App;
