            svg.append('g').attr('transform', 'translate(1,300)').call(d3.axisBottom(xAxis))
            svg.append('g').call(xAxis)
            svg.append('g')
            .selectAll("dot")
            .data(parsedData)
            .join("circle")
                .attr("cx", function (d) { return xAxis(d.x); } )
